import React, { Component } from 'react';
import axios from 'axios';

class Profile extends Component {
  state = {
    error: null,
    isLoaded: false,
    username: undefined,
    name: undefined,
    profile_img: undefined,
    userInfo: ''
  };

  componentDidMount = () => {
    this.getUser()
  }

  getUser = async (username) => {
    username = "elbachimilio";
    try {
      const userInfo = await axios.get(`http://localhost:5000/api/users/${username}`)
      this.setState({
        userInfo,
        isLoaded: true
      })
    } catch (err) {
      console.log(err)
    }
  }

  getRandomPhoto = async () => {
    try {
      const randomPhoto = await axios.get(`http://localhost:5000/api/photos/random`)
      this.setState({
        randomPhoto,
        isLoaded: true
      })
    } catch (err) {
      console.log(err)
    }
  }


  showCard = () => {
    return (
      <div className="card">
        <div className="card-image">
          <figure className="image is-4by3">
            <img src="https://bulma.io/images/placeholders/1280x960.png" alt="Placeholder image" />
          </figure>
        </div>
        <div className="card-content">
          <div className="media">
            <div className="media-left">
              <figure className="image is-48x48">
                <img src="https://bulma.io/images/placeholders/96x96.png" alt="Placeholder image" />
              </figure>
            </div>
            <div className="media-content">
              <p className="title is-4">John Smith</p>
              <p className="subtitle is-6">@johnsmith</p>
            </div>
          </div>

          <div className="content">
            Lorem ipsum dolor sit amet, consectetur adipiscing elit.
          Phasellus nec iaculis mauris. <a>@bulmaio</a>.
          <a href="#">#css</a> <a href="#">#responsive</a>
            <br />
            <time dateTime="2016-1-1">11:09 PM - 1 Jan 2016</time>
          </div>
        </div>
      </div>
    )
  }

  showUserInfo = (userInfo) => {
    console.log("====>Ddfkdgmdflgmdsfkgmdskgmsdf ===> ", userInfo)
    console.log("THE TYPEOF USERINFO IS: ", typeof (userInfo))
    let keys = Object.keys(userInfo)
    let currentUser = keys.map((key, i) => {
      console.log("IN CURRENT USER-keys", currentUser)
      return typeof (userInfo[key]) == "object" ? <li>this is an object</li> : <li key={i}>{userInfo[key]}</li>
    })
    return currentUser
    // data: 
    // key: {key}, 
  }

  render() {
    const { error, isLoaded } = this.state;
    if (error) {
      return <div>Error: {error.message}</div>;
    } else if (!isLoaded) {
      return <div>Loading...</div>;
    } else {
      return (
        <div>
          <ul>
            {this.showUserInfo(this.state.userInfo.data)}
          </ul>

          {this.showCard()}
        </div>
        // <ul>
        //   <li>{JSON.stringify(this.state.userInfo)}</li>
        // </ul>
      );
    }
  }
}

export default Profile;






