import React, { Component } from 'react'
import api from '../../api'

class Signup extends Component {
  constructor(props) {
    super(props)
    this.state = {
      first_name: '',
      last_name: '',
      username: '',
      password: '',
      email: '',
      bio: '',
      location: '',
      message: null,
    }
    this.handleInputChange = this.handleInputChange.bind(this)
  }

  handleInputChange(event) {
    this.setState({
      [event.target.name]: event.target.value,
    })
  }

  handleClick(e) {
    e.preventDefault()
    let data = {
      username: this.state.username,
      name: this.state.name,
      password: this.state.password,
    }
    api
      .signup(data)
      .then(result => {
        console.log('SUCCESS!')
        this.props.history.push('/') // Redirect to the home page
      })
      .catch(err => this.setState({ message: err.toString() }))
  }

  displaySignup = () => {
    return (
      <div>
        <div className="field">
          <label className="label">First name</label>
          <div className="control">
            <input className="input" type="text" placeholder="Ex. Elba" value={this.state.first_name} onChange={e => this.handleInputChange(e)} />
          </div>
        </div>
        <div className="field">
          <label className="label">Last name</label>
          <div className="control">
            <input className="input" type="text" placeholder="Ex. Chimilio" value={this.state.last_name} onChange={e=> this.handleInputChange} />
          </div>
        </div>

        <div className="field">
          <label className="label">Username</label>
          <div className="control has-icons-left has-icons-right">
            <input className="input is-success" type="text" placeholder="Ex. elbachimilio" value={this.state.username} onChange={this.handleInputChange} />
            <span className="icon is-small is-left">
              <i className="fas fa-user"></i>
            </span>
            <span className="icon is-small is-right">
              <i className="fas fa-check"></i>
            </span>
          </div>
          <p className="help is-success">This username is available</p>
        </div>

        <div className="field">
          <label className="label">Email</label>
          <div className="control has-icons-left has-icons-right">
            <input className="input is-danger" type="email" placeholder="Ex. elbachimilio@gmail.com" value={this.state.email} onChange={this.handleInputChange} />
            <span className="icon is-small is-left">
              <i className="fas fa-envelope"></i>
            </span>
            <span className="icon is-small is-right">
              <i className="fas fa-exclamation-triangle"></i>
            </span>
          </div>
          <p className="help is-danger">This email is invalid</p>
        </div>

        <div className="field">
          <label className="label">Type of Artist</label>
          <div className="control">
            <div className="select">
              <select>
                <option>Photographer</option>
                <option>Model</option>
                <option>Stylist</option>
              </select>
            </div>
          </div>
        </div>

        <div className="field">
          <label className="label">Bio</label>
          <div className="control">
            <textarea className="textarea" placeholder="This is an amazing website. Good job Elba! You are so cool! " value={this.state.bio} onChange={this.handleInputChange}></textarea>
          </div>
        </div>

        {/* <div class="field">
          <div class="control">
            <label class="checkbox">
              <input type="checkbox" />
              I agree to the <a href="#">terms and conditions</a>
            </label>
          </div>
        </div> */}

        {/* <div class="field">
          <div class="control">
            <label class="radio">
              <input type="radio" name="question" />
              Yes
    </label>
            <label class="radio">
              <input type="radio" name="question" />
              No
    </label> */}
        {/* </div>
        </div > */}

        <div className="field is-grouped">
          <div className="control">
            <button className="button is-link" onClick={e => this.handleClick(e)}>Submit</button>
          </div>
          {/* <div className="control">
            <button className="button is-text">Cancel</button>
          </div> */}
        </div>
      </div >
    )
  }

  render() {
    return (
      <div>
        {this.displaySignup()}
      </div>
      // <div className="Signup">
      //   <h2>Signup</h2>
      //   <form>
      //     Email:{' '}
      //     <input
      //       type="text"
      //       value={this.state.email}
      //       name="email"
      //       onChange={this.handleInputChange}
      //     />{' '}
      //     <br />
      //     Username:{' '}
      //     <input
      //       type="text"
      //       value={this.state.username}
      //       name="username"
      //       onChange={this.handleInputChange}
      //     />{' '}
      //     <br />
      //     First name:{' '}
      //     <input
      //       type="text"
      //       value={this.state.first_name}
      //       name="firstName"
      //       onChange={this.handleInputChange}
      //     />{' '}
      //     <br />
      //     Last name:{' '}
      //     <input
      //       type="text"
      //       value={this.state.last_name}
      //       name="lastName"
      //       onChange={this.handleInputChange}
      //     />{' '}
      //     <br />
      //     Password:{' '}
      //     <input
      //       type="password"
      //       value={this.state.password}
      //       name="password"
      //       onChange={this.handleInputChange}
      //     />{' '}
      //     <br />
      //     Type:{' '}
      //     <input
      //       type="text"
      //       // value={this.state.type}
      //       name="type"
      //       onChange={this.handleInputChange}
      //     />{' '}
      //     <br />
      //     Bio:{' '}
      //     <input
      //       type="text"
      //       // value={this.state.type}
      //       name="biography"
      //       onChange={this.handleInputChange}
      //     />{' '}
      //     <br />
      //     Location:{' '}
      //     <input
      //       type="text"
      //       // value={this.state.type}
      //       name="location"
      //       onChange={this.handleInputChange}
      //     />{' '}
      //     <br />
      //     <button onClick={e => this.handleClick(e)}>Signup</button>
      //   </form>
      //   {this.state.message && (
      //     <div className="info info-danger">{this.state.message}</div>
      //   )}
      // </div>
    )
  }
}

export default Signup;
