    super(props)
    this.state = {
      username: '',
      name: '',
      password: '',
      email: '',
      bio: '',
      location: '',
      message: null,
    }
    this.handleInputChange = this.handleInputChange.bind(this)
  }

  handleInputChange(event) {
    this.setState({
