import React, { Component } from 'react'
import axios from 'axios';
import Carousel from 'react-bootstrap/Carousel'
import Chat from './Chat';
import '../../styling/index.scss'
// import serverUrl from '../../configServer'

class People extends Component {

  state = {
    allUsers: []
  }

  componentDidMount() {
    // console.log('ppl')
    this.showAllUsers()

  }
  showAllUsers = () => {
    axios.get('http://localhost:5000/api/allUsers', { withCredentials: true })
      .then(res => {
        // console.log(res.data.allUsersFromDB)
        this.setState({
          allUsers: res.data.allUsersFromDB
        })
      }).catch(err => console.log(err))
  }

  showEachUser = () => {
    let user = this.state.allUsers.map(eachUser => eachUser.username)
    console.log("showing each user", user)
    return user;
  }

  showUserCards = () => {
    return (
      <div>
    <div className="profile-card">
        <div className="profile-info">
          <img src={this.showEachUser} />
          <h1>Name: {this.state.first_name} {this.state.last_name}</h1>
          <h1>username: {this.state.username}</h1>
          <p>{this.state.bio}</p>
          <p>I'm a {this.state.userType}</p>
        </div>
      </div>
        <p><li>{this.showEachUser()}</li></p>


      </div>
    )
  }

  render() {
    return (
      <div>
        In People component
        {this.showUserCards()}

        {/* <Chat /> */}
      </div>
    )
  }
}

export default People;
