import React, { Component } from 'react';
import axios from 'axios';
import api from '../../api';
import '../../styling/index.scss'
// import { runInNewContext } from 'vm';
// import serverUrl from '../../configServer'

class Profile extends Component {
  state = {
    error: null,
    isLoaded: false,
    first_name: '',
    last_name: '',
    username: '',
    email: '',
    bio: '',
    location: '',
    userType: '',
    profilePhoto: ''
  }

  async componentDidMount() {
    let currentUser = await api.getLocalStorageUser()
    // console.log(currentUser)
    this.setState({
      first_name: currentUser.first_name,
      last_name: currentUser.last_name,
      username: currentUser.username,
      email: currentUser.email,
      bio: currentUser.bio,
      location: currentUser.location,
      userType: currentUser.userType,
      imageUrl: currentUser.imageUrl,
      isLoaded: true
    })
    // this.getRandomPhoto()
  }


  handleClick = (e) => {
    e.preventDefault()
    let data = {
      first_name: this.state.first_name,
      last_name: this.state.last_name,
      username: this.state.username,
      password: this.state.password,
      email: this.state.email,
      imageUrl: this.state.imageUrl,
      bio: this.state.bio,
      location: this.state.location,
      userType: this.state.userType,
      artwork: this.state.artwork

    }
    console.log("This is the data =>", data)
    api
      .signup(data)
      .then(result => {
        console.log('SUCCESSFULLY SIGNED UP!')
        this.props.checkLogin()
        this.props.history.push('/login') // Redirect to the home page
      })
      .catch(err => this.setState({ message: err.toString() }))
  }

  getRandomPhoto = () => {
    axios.get('http://localhost:5000/api/random-photo', { withCredentials: true })
      .then(res => {
        let thePhoto = res.data.pic.urls.regular
        this.setState({
          profilePhoto: thePhoto
        })
      }).catch(err => console.log(err))
  }


  showCard = () => {
    return (
      <div className="profile-card">
        <div className="profile-info">
          <img src={this.state.imageUrl} />
          <h1>Name: {this.state.first_name} {this.state.last_name}</h1>
          <h1>username: {this.state.username}</h1>
          <p>{this.state.bio}</p>
          <p>I'm a {this.state.userType}</p>
        </div>
      </div>
    )
  }

  render() {

    const { error, isLoaded } = this.state;
    if (error) {
      return <div>Error: {error.message}</div>;
    } else if (!isLoaded) {
      return <div>Loading...</div>;
    } else {
      return (
        <div>
          <button onClick={e => this.handleClick(e)}>Edit User</button>
          {/* <img src={this.getRandomPhoto()} /> */}
          {this.showCard()}
        </div>

      );
    }
  }
}

export default Profile;


