import React, { Component } from 'react';
import axios from 'axios';
import api from '../../api';
import '../../styling/index.scss'
import Modal from './Modal'
// import { runInNewContext } from 'vm';
import serverUrl from '../../configServer'

class Profile extends Component {
  constructor(props) {
    super(props);

    this.state = {
      error: null,
      isLoaded: false,
      first_name: '',
      last_name: '',
      username: '',
      email: '',
      bio: '',
      location: '',
      userType: '',
      profilePhoto: '',
      modalState: false
    };

    this.toggleModal = this.toggleModal.bind(this);
  }

  async componentDidMount() {
    let currentUser = await api.getLocalStorageUser()
    // console.log(currentUser)
    this.setState({
      userId: currentUser._id,
      first_name: currentUser.first_name,
      last_name: currentUser.last_name,
      username: currentUser.username,
      email: currentUser.email,
      bio: currentUser.bio,
      location: currentUser.location,
      userType: currentUser.userType,
      imageUrl: currentUser.imageUrl,
      isLoaded: true
    })
    // this.getRandomPhoto()
  }

  toggleModal() {
    this.setState((prev, props) => {
      const newState = !prev.modalState;

      return { modalState: newState };
    });
  }


  getRandomPhoto = () => {
    axios.get(`${serverUrl}/random-photo`, { withCredentials: true })
      .then(res => {
        let thePhoto = res.data.pic.urls.regular
        this.setState({
          profilePhoto: thePhoto
        })
      }).catch(err => console.log(err))
  }


  showCard = () => {
    return (
      <div className="profile-card">
        <div className="profile-info">
          <img src={this.state.imageUrl} />
          <h1>Name: {this.state.first_name} {this.state.last_name}</h1>
          <h1>username: {this.state.username}</h1>
          <p>{this.state.bio}</p>
          <p>I'm a {this.state.userType}</p>
        </div>

        <Modal
          closeModal={this.toggleModal}
          userId={this.state.userId}
          modalState={this.state.modalState}
          title="Edit Profile" />

      </div>
    )
  }

  render() {

    const { error, isLoaded } = this.state;
    if (error) {
      return <div>Error: {error.message}</div>;
    } else if (!isLoaded) {
      return <div>Loading...</div>;
    } else {
      return (
        <div>
          <Modal />
          {/* <button onClick={this.openModal()}>Edit User</button> */}
          {/* <img src={this.getRandomPhoto()} /> */}
          {this.showCard()}
        </div>

      );
    }
  }
}

export default Profile;


