import React, { Component } from 'react';
import axios from 'axios';
import api from '../../api';
// import { runInNewContext } from 'vm';

class Profile extends Component {
  state = {
    error: null,
    isLoaded: false,
    first_name: '',
    last_name: '',
    username: '',
    email: '',
    bio: '',
    location: '',
    userType: '',
    profilePhoto: ''
  }

  async componentDidMount() {
    let currentUser = await api.getLocalStorageUser()
    // console.log(currentUser)
    this.setState({
      first_name: currentUser.first_name,
      last_name: currentUser.last_name,
      username: currentUser.username,
      email: currentUser.email,
      bio: currentUser.bio,
      location: currentUser.location,
      userType: currentUser.userType,
      profilePhoto: currentUser.profilePhoto,
      isLoaded: true
    })
    // this.getRandomPhoto()
  }

  showCard = () => {
    return (
      <div className="card">
        <div className="card-image">
          <figure className="image is-4by3">
            <img src={this.state.profilePhoto} alt="Placeholder image" style={{ "width": "380px", height: "500px" }} />
          </figure>
        </div>
        <div className="card-content">
          <div className="media">
            <div className="media-left">
              <figure className="image is-48x48">
                <img src="../images/profile_1.jpeg" alt="Placeholder image" />
              </figure>
            </div>
            <div className="media-content">
              <p className="title is-4">{this.state.first_name}</p>
              <p className="subtitle is-6">{this.state.email}</p>
              <p>You're a {this.state.userType}</p>
            </div>
          </div>

          <div className="content">
            {this.state.bio}.
            <br />
            <time dateTime="2016-1-1">11:09 PM - 1 Jan 2016</time>
          </div>
        </div>
      </div>
    )
  }

  render() {

    const { error, isLoaded } = this.state;
    if (error) {
      return <div>Error: {error.message}</div>;
    } else if (!isLoaded) {
      return <div>Loading...</div>;
    } else {
      return (
        <div>
          {/* <img src={this.getRandomPhoto()} /> */}
          {this.showCard()}
        </div>

      );
    }
  }
}

export default Profile;
